package com.javaex.ex04;

public class Ex032 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
//		1 부터 10까지 합 구하는 프로그램
		
		int i;
		int sum;
		
		
		sum=0;
		
		for ( i=1 ; i<=10 ; i++ ) {
			sum = sum+i ;
			System.out.println(i+"까지의 합은 "+sum);	
		}
		
	}

}
