package com.javaex.ex04;

public class Ex034 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
//		2단부터 9단까지 연속 출력 for 사용
		
		int i;
		int dan;
		
		for ( dan=2; dan<=9 ; dan++) {
		
			for ( i=1; i<=9; i++) {
				System.out.println(dan+"*"+i+"=" +dan*i);
			}
			
			
		}
		
		
		
		}
		

}
